  `ten` varchar(150) COLLATE utf8mb4_unicode_ci NOT NULL,
  `ten_khongdau` varchar(160) COLLATE utf8mb4_unicode_ci NOT NULL,
  `chuong` int(11) NOT NULL DEFAULT 0,
  `tacgia` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL,
  `nguon` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `trangthai` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'Đang cập nhật',
  `gioithieu` text COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `danhgia` float NOT NULL DEFAULT 0,
  `ngaytao` timestamp NOT NULL DEFAULT current_timestamp(),
  `capnhat` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `truyen`
--

INSERT INTO `truyen` (`id`, `hinh`, `ten`, `ten_khongdau`, `chuong`, `tacgia`, `nguon`, `trangthai`, `gioithieu`, `danhgia`, `ngaytao`, `capnhat`) VALUES
(10, 'ben-rang-tuyet-son-swami-amar-jyoti-(nguyen-phong-phong-tac).jpg', 'bên rặng Tuyết sơn', 'ben-rang-tuyet-son', 3, 'Swami Amar Jyoti (Nguyên Phong \'phong tác\')', 'TuSach.vn', 'Hoàn thành', 'B&ecirc;n rặng Tuyết sơn l&agrave; c&acirc;u chuyện th&uacute; vị kể về một ch&agrave;ng trai đi t&igrave;m mối li&ecirc;n hệ giữa con người v&agrave; vũ trụ qua thuật yoga. Cuối c&ugrave;ng, anh đ&atilde; được khai s&aacute;ng v&agrave; nh&igrave;n thấy Thượng đế. Kể từ đ&oacute; anh chu du khắp nơi để gi&uacute;p mọi người v&agrave; truyền dạy cho họ những g&igrave; anh lĩnh hội được.', 0, '2022-05-23 09:36:30', '2022-05-23 17:25:51'),
(11, 'danh-thuc-nang-luc-vo-han-anthony-robbins.jpg', 'Đánh Thức Năng Lực Vô Hạn', 'danh-thuc-nang-luc-vo-han', 3, 'Anthony Robbins', 'TuSach.vn', 'Hoàn thành', 'Khi thấy một ai đ&oacute; th&agrave;nh đạt ta thường tự hỏi &ldquo;l&agrave;m thế n&agrave;o để anh ta c&oacute; được th&agrave;nh quả ấy?&rdquo;, mặc d&ugrave; x&eacute;t về xuất ph&aacute;t điểm c&oacute; thể họ kh&ocirc;ng đủ tư chất bằng ta, thế nhưng sao họ lại sớm đạt được sự th&agrave;nh c&ocirc;ng như &yacute;&hellip;<br />Sự kh&aacute;c biệt n&agrave;y thường đơn giản l&agrave; v&igrave; bạn lu&ocirc;n c&oacute; những nỗi sợ h&atilde;i tiềm ẩn trong tư tưởng, nhận thức của m&igrave;nh &ndash; N&oacute; được t&iacute;ch lũy v&agrave; ph&aacute;t triển, đ&ocirc;i l&uacute;c lại d&acirc;ng cao để tự bảo vệ, ngăn chặn những rủi ro c&oacute; thể xảy đến, dần dần biến th&agrave;nh một lớp vỏ bọc &ndash; đ&oacute; ch&iacute;nh l&agrave; trở lực của th&agrave;nh c&ocirc;ng.<br />Vận dụng được nguồn năng lực v&ocirc; hạn, kh&ocirc;ng những loại bỏ được sự sợ h&atilde;i m&agrave; c&ograve;n cho ta một th&aacute;i độ sống t&iacute;ch cực, tự tin, d&aacute;m nghĩ d&aacute;m l&agrave;m. Đ&oacute; l&agrave; nguồn nhi&ecirc;n liệu kh&ocirc;ng bao giờ cạn kiệt đang chờ sự khởi động đ&uacute;ng l&uacute;c, nhịp nh&agrave;ng để mọi người c&oacute; thể đạt đến đỉnh điểm của th&agrave;nh c&ocirc;ng.<br />Con người chỉ c&oacute; thể chuyển đổi cuộc sống của m&igrave;nh một c&aacute;ch ngoạn mục, thần kỳ khi biết t&igrave;m c&aacute;ch khai th&ocirc;ng, giải ph&oacute;ng nguồn năng lực tiềm ẩn đ&oacute;.<br />Nội dung s&aacute;ch sẽ l&agrave; hồi chu&ocirc;ng Đ&aacute;nh thức nguồn năng lực v&ocirc; hạn trong mỗi ch&uacute;ng ta.', 0, '2022-05-23 10:11:48', '2022-05-23 10:34:07'),
(12, 'loi-song-toi-gian-cua-nguoi-nhat-sasaki-fumio.jpg', 'Lối Sống Tối Giản Của Người Nhật', 'loi-song-toi-gian-cua-nguoi-nhat', 3, 'Sasaki Fumio', 'TuSach.vn', 'Hoàn thành', 'Lối sống tối giản l&agrave; c&aacute;ch sống cắt giảm vật dụng xuống c&ograve;n mức tối thiểu. V&agrave; c&ugrave;ng với cuộc sống &iacute;t đồ đạc, ta c&oacute; thể để t&acirc;m nhiều hơn tới hạnh ph&uacute;c, đ&oacute; ch&iacute;nh l&agrave; chủ đề của cuốn s&aacute;ch n&agrave;y.<br />Chẳng c&oacute; ai từ khi sinh ra đ&atilde; c&oacute; t&agrave;i sản, đồ đạc g&igrave; trong tay. Vậy n&ecirc;n bất cứ ai khi mới ch&agrave;o đời đều l&agrave; những người sống tối giản. Cứ mỗi lần bạn sở hữu trong tay những đồ d&ugrave;ng hơn mức cần thiết l&agrave; một lần bạn lấy mất tự do của ch&iacute;nh m&igrave;nh. Gi&aacute; trị bản th&acirc;n ch&uacute;ng ta kh&ocirc;ng đo bằng những đồ d&ugrave;ng m&agrave; ch&uacute;ng ta sở hữu. Những đồ d&ugrave;ng n&agrave;y chỉ cho ch&uacute;ng ta một ch&uacute;t cảm gi&aacute;c hạnh ph&uacute;c nhất thời m&agrave; th&ocirc;i. Mang theo những đồ d&ugrave;ng hơn mức cần thiết sẽ lấy hết thời gian, năng lượng của bạn. Khi nhận ra được điều đ&oacute;, tức l&agrave; bạn đ&atilde; bắt đầu trở th&agrave;nh một người sống tối giản.', 0, '2022-05-23 10:37:24', '2022-05-23 17:27:01'),
(13, 'tuoi-tre-dang-gia-bao-nhieu-rosie-nguyen.jpg', 'Tuổi Trẻ Đáng Giá Bao Nhiêu', 'tuoi-tre-dang-gia-bao-nhieu', 2, 'Rosie Nguyễn', 'TuSach.vn', 'Hoàn thành', '&ldquo;Bạn hối tiếc v&igrave; kh&ocirc;ng nắm bắt lấy một cơ hội n&agrave;o đ&oacute;, chẳng c&oacute; ai phải mất ngủ.<br />Bạn trải qua những ng&agrave;y th&aacute;ng nhạt nhẽo với c&ocirc;ng việc bạn căm gh&eacute;t, người ta chẳng hề bận l&ograve;ng.<br />Bạn c&oacute; chết m&ograve;n nơi x&oacute; tường với những ước mơ dang dở, đ&oacute; kh&ocirc;ng phải l&agrave; việc của họ.<br />Suy cho c&ugrave;ng, quyết định l&agrave; ở bạn. Muốn c&oacute; điều g&igrave; hay kh&ocirc;ng l&agrave; t&ugrave;y bạn.<br />N&ecirc;n h&atilde;y l&agrave;m những điều bạn th&iacute;ch. H&atilde;y đi theo tiếng n&oacute;i tr&aacute;i tim. H&atilde;y sống theo c&aacute;ch bạn cho l&agrave; m&igrave;nh n&ecirc;n sống.<br />V&igrave; sau tất cả, chẳng ai quan t&acirc;m.&rdquo;<br />Nhận định<br />&ldquo;T&ocirc;i đ&atilde; đọc quyển s&aacute;ch n&agrave;y một c&aacute;ch th&iacute;ch th&uacute;. C&oacute; nhiều kiến thức v&agrave; kinh nghiệm hữu &iacute;ch, những điều mới mẻ ngay cả với người gần trung ni&ecirc;n như t&ocirc;i.<br />Tuổi trẻ đ&aacute;ng gi&aacute; bao nhi&ecirc;u? được t&aacute;c giả chia l&agrave;m 3 phần: HỌC, L&Agrave;M, ĐI.<br />Nhưng t&ocirc;i thấy cuốn s&aacute;ch c&ograve;n thể hiện một phần thứ tư nữa, đ&oacute; l&agrave; ĐỌC.<br />H&atilde;y đọc s&aacute;ch, nếu bạn đọc s&aacute;ch một c&aacute;ch bền bỉ, sẽ đến l&uacute;c bạn bị th&ocirc;i th&uacute;c kh&ocirc;ng ngừng bởi &yacute; muốn viết n&ecirc;n cuốn s&aacute;ch của ri&ecirc;ng m&igrave;nh.<br />Nếu t&ocirc;i c&ograve;n ở tuổi đ&ocirc;i mươi, hẳn l&agrave; t&ocirc;i sẽ đọc Tuổi trẻ đ&aacute;ng gi&aacute; bao nhi&ecirc;u? nhiều hơn một lần.&rdquo;', 0, '2022-05-23 10:43:56', '2022-05-23 18:05:15'),
(14, 'bat-song-cam-xuc-–-bi-mat-luc-hap-dan-ori-brafman---rom-brafman.jpg', 'Bắt Sóng Cảm Xúc – Bí Mật Lực Hấp Dẫn', 'bat-song-cam-xuc-–-bi-mat-luc-hap-dan', 8, 'Ori Brafman - Rom Brafman', 'TuSach.vn', 'Hoàn thành', 'L&agrave; cuốn cẩm nang gợi mở những &yacute; tưởng mới nhất về h&igrave;nh th&aacute;i giao tiếp giữa con người với nhau, &ldquo;Bắt S&oacute;ng Cảm X&uacute;c&rdquo; c&ograve;n l&agrave; một chuyến đi th&uacute; vị kh&aacute;m ph&aacute; c&aacute;ch bạn h&ograve;a nhịp với thế giới quan xung quanh m&igrave;nh, l&agrave;m thay đổi&nbsp; c&aacute;ch nghĩ của bạn trong những khoảnh khắc khi bạn rơi v&agrave;o v&ugrave;ng cảm x&uacute;c đặc biệt v&agrave; mọi thứ dường như đ&atilde; được đặt v&agrave;o vĩ đạo.<br />Đến từ hai t&aacute;c giả nổi tiếng của cuốn Sway ( Lối M&ograve;n Của Tư Duy Cảm T&iacute;nh),&rdquo; Bắt S&oacute;ng Cảm X&uacute;c&rdquo; l&agrave; cuộc kh&aacute;m ph&aacute; t&acirc;m l&yacute; th&uacute; vị về những động lực v&agrave; cảm x&uacute;c khiến ch&uacute;ng ta như bị h&uacute;t v&agrave;o những con người nhất định v&agrave; đ&ocirc;i b&ecirc;n trở th&agrave;nh th&acirc;n thiết với nhau trong c&aacute;c hoạt động hoặc c&aacute;c t&igrave;nh huống c&oacute; li&ecirc;n quan.<br />Nếu việc bắt s&oacute;ng cảm x&uacute;c mang đến những điều kỳ diệu đến vậy, th&igrave; liệu ch&uacute;ng ta c&oacute; thể tạo ra mối gắn kết đặc biệt đ&oacute; trong một cuộc hẹn h&ograve; hoặc c&ocirc;ng việc hay kh&ocirc;ng? V&igrave; sao ngay lập tức ch&uacute;ng ta cảm mến người n&agrave;y m&agrave; kh&ocirc;ng phải l&agrave; người kh&aacute;c? C&oacute; c&aacute;ch n&agrave;o để tạo n&ecirc;n những trải nghiệm đặc biệt như vậy kh&ocirc;ng?', 0, '2022-05-23 10:55:17', '2022-05-24 11:48:26'),
(15, 'vo-thuong-nguyen-bao-trung.jpg', 'Vô Thường', 'vo-thuong', 4, 'Nguyễn Bảo Trung', 'TuSach.vn', 'Đang cập nhật', 'Cuốn s&aacute;ch l&agrave; g&oacute;c nh&igrave;n đầy nh&acirc;n văn của vị b&aacute;c sĩ h&agrave;ng ng&agrave;y chứng kiến những mảnh đời chấp chới giữa hai bờ sinh tử. Con người ta sinh ra, b&agrave;n tay nắm chặt. Con người ta chết đi, hai tay bu&ocirc;ng th&otilde;ng, được mất bại th&agrave;nh bỗng chốc ho&aacute; hư kh&ocirc;ng.<br />&ldquo;Hy sinh v&igrave; người kh&aacute;c lu&ocirc;n cho hương thơm bay ngược chiều gi&oacute;. G&aacute;nh nặng v&igrave;&nbsp;t&igrave;nh y&ecirc;u&nbsp;lu&ocirc;n song h&agrave;nh c&ugrave;ng sức mạnh v&ocirc; song. Bất cứ gỗ đ&aacute; n&agrave;o chạm phải t&igrave;nh y&ecirc;u đều trở n&ecirc;n bao dung mềm mại.<br />T&ocirc;i thấy l&ograve;ng m&igrave;nh bỗng chật. Những g&igrave; trước đ&acirc;y t&ocirc;i cho l&agrave; đ&uacute;ng, b&acirc;y giờ t&ocirc;i đ&acirc;m nghi ngờ. Những g&igrave; trước đ&acirc;y t&ocirc;i lu&ocirc;n theo đuổi, gi&agrave;nh giật để c&oacute;, b&acirc;y giờ thấy chẳng c&ograve;n quan trọng nữa.<br />Những tay th&eacute;t ra lửa, những tay sừng sỏ m&agrave; t&ocirc;i từng k&iacute;nh nể, bỗng dưng t&ocirc;i thấy họ b&igrave;nh thường. Họ cố gắng d&ugrave;ng đ&ocirc;i b&agrave;ntay chứng tỏ m&igrave;nh, khuếch trương m&igrave;nh, những c&aacute;i họ c&oacute; được chỉ l&agrave; thứ trơ trẽn. Họ kh&ocirc;ng b&igrave;nh y&ecirc;n tr&ecirc;n vật chất họ c&oacute; được. Họ kho&aacute;c những chiếc &aacute;o sang trọng, tay đeo đầy những kim cương, xịt to&agrave;n nước hoa hảo hạng, nhưng kh&ocirc;ng bao giờ c&oacute; m&ugrave;i hương thanh tao, dịu ngọt, toả lan khắp bầu trời.<br />T&ocirc;i bị &aacute;m ảnh, v&igrave; trong t&ocirc;i ho&agrave;i thai một lối sống. T&ocirc;i muốn tho&aacute;t khỏi b&agrave;n tay của ch&iacute;nh m&igrave;nh. T&ocirc;i l&agrave; người t&igrave;m kiếm b&agrave;n tay đẹp.B&agrave;n tay biết dang ra, biết sẻ chia l&agrave; b&agrave;n tay đẹp. B&agrave;n tay biết nắm lấy tay người bất hạnh hơn m&igrave;nh để c&ugrave;ng bước l&agrave; b&agrave;n tay đẹp. B&agrave;n tay biết n&acirc;ng niu, g&igrave;n giữ c&aacute;i đẹp, c&aacute;i ch&acirc;n, c&aacute;i thiện, l&agrave; b&agrave;n tay đẹp. V&agrave; h&agrave;ng ng&agrave;n định nghĩa về b&agrave;n tay đẹp kh&aacute;c.C&oacute; b&agrave;n tay cầm nắm rất nhiều, c&oacute; thể điều khiển người kh&aacute;c. C&oacute; b&agrave;n tay xo&egrave; ra ăn xin từng đồng lẻ bố th&iacute;. C&oacute; b&agrave;n tay kh&eacute;o l&eacute;o l&agrave;m n&ecirc;n những tuyệt t&aacute;c nghệ thuật.<br />C&oacute; b&agrave;n tay vụng về chỉ l&agrave;m đổ vỡ mọi thứ khi chạm v&agrave;o. C&oacute; b&agrave;n tay cho đi. C&oacute; b&agrave;n tay giữ lại. Nhưng khi về với đất, b&agrave;n tay n&agrave;o cũng rỗng. Rỗng tuyệt đối.Vậy sao kh&ocirc;ng ướp hương cho đ&ocirc;i tay m&igrave;nh, t&ocirc;i tự hỏi l&ograve;ng như thế. C&oacute; h&agrave;ng ng&agrave;n c&aacute;ch ướp hương,&nbsp; ướp hương th&aacute;nh thiện, &acirc;m thầm, khi&ecirc;m cung, b&eacute; nhỏ, m&agrave; hương thơm lại bay vượt mọi kh&ocirc;ng gian.<br />Khi sinh ra, tay t&ocirc;i nắm chặt. Khi chết đi tay t&ocirc;i bu&ocirc;ng th&otilde;ng. Từ nắm chặt đến bu&ocirc;ng th&otilde;ng, một h&agrave;nh tr&igrave;nh d&agrave;i đầy nụ cười hạnh ph&uacute;c v&agrave; nước mắt đau thương.&rdquo;', 0, '2022-05-23 11:04:15', '2022-05-23 17:24:32'),
(16, 'mat-troi-moc-trong-bong-hoang-hon-moc-tu-lang.jpg', 'Mặt Trời Mọc Trong Bóng Hoàng Hôn', 'mat-troi-moc-trong-bong-hoang-hon', 5, 'Mộc Tử Lãng', 'TuSach.vn', 'Đang cập nhật', 'Cuộc sống, kh&ocirc;ng phải cứ muốn l&agrave; được, cứ nu&ocirc;i hy vọng l&agrave; sẽ th&agrave;nh c&ocirc;ng. T&ocirc;i cũng muốn sống một cuộc sống tươi đẹp như bao người kia, cũng muốn đến b&ecirc;n cạnh, tr&ograve; chuyện, hỏi han, chăm s&oacute;c c&ocirc; g&aacute;i ấy, cũng muốn được an nh&agrave;n đ&oacute;n một c&aacute;i Tết đầy &yacute; nghĩa b&ecirc;n gia đ&igrave;nh v&agrave; người th&acirc;n,&hellip; Nhưng tất cả kh&ocirc;ng như t&ocirc;i muốn! Từ &ldquo;ước mơ&rdquo; h&igrave;nh như qu&aacute; xa vời, m&atilde;i m&atilde;i kh&ocirc;ng xuất hiện trong cuộc đời t&ocirc;i&hellip;<br />Ng&agrave;y ấy nắng nhạt, &aacute;nh mặt trời hắt l&ecirc;n vai t&ocirc;i, nhưng trong l&ograve;ng t&ocirc;i c&oacute; một cảm gi&aacute;c v&ocirc; c&ugrave;ng kh&oacute; chịu. C&ocirc; ấy đi rồi, đi thật rồi! Ước mơ bao nhi&ecirc;u năm sống v&igrave; ch&iacute;nh nghĩa của ch&uacute;ng t&ocirc;i cuối c&ugrave;ng đổi được c&aacute;i g&igrave;? C&ocirc; ấy hy sinh v&igrave; bắt tội phạm bu&ocirc;n b&aacute;n trẻ em, cả đội cảnh s&aacute;t ch&uacute;ng t&ocirc;i thức trắng mấy đ&ecirc;m, cuối c&ugrave;ng tr&igrave;nh to&agrave; chỉ ấm ức nghe c&acirc;u &ldquo;Chưa đủ chứng cứ, nghi phạm v&ocirc; tội, ph&oacute;ng th&iacute;ch tại chỗ!&rdquo;. Thật nực cười biết bao nhi&ecirc;u, đ&uacute;ng kh&ocirc;ng?', 0, '2022-05-23 11:22:03', '2022-05-23 11:23:56'),
(17, 'ranh-gioi-rain8x.jpg', 'Ranh Giới', 'ranh-gioi', 4, 'Rain8x', 'TuSach.vn', 'Đang cập nhật', 'Đ&acirc;y l&agrave; một c&acirc;u truyện n&oacute;i về cuộc sống, gia đ&igrave;nh, t&igrave;nh y&ecirc;u, t&acirc;m sinh l&yacute; con người qua những trải nghiệm của một thanh ni&ecirc;n thuộc thế hệ nửa đầu 8x. Với những đấu tranh nội t&acirc;m gay gắt khi đứng trước ngưỡng cửa của cuộc đời, bước v&agrave;o mối t&igrave;nh đầu. Giữa tốt đẹp v&agrave; xấu xa, giữa thi&ecirc;n thần v&agrave; &aacute;c quỷ, t&igrave;nh y&ecirc;u v&agrave; bản năng, &nbsp;đ&ocirc;i khi chỉ c&aacute;ch nhau bằng một &ldquo;Ranh Giới&rdquo; thật mong manh&hellip; buộc l&yacute; tr&iacute; sẽ phải lựa chọn&hellip;<br />Truyện lấy bối cảnh đầu ti&ecirc;n v&agrave;o năm 2001. Mở đầu l&agrave; những g&oacute;c nh&igrave;n của cậu thanh ni&ecirc;n ấy đang ở độ tuổi dậy th&igrave; với những t&ograve; m&ograve; về giới t&iacute;nh&hellip;V&agrave; nhen nh&uacute;m mối t&igrave;nh đầu với c&ocirc; bạn c&ugrave;ng lớp. Bắt đầu l&agrave; những đấu tranh giữa t&igrave;nh y&ecirc;u trong s&aacute;ng v&agrave; những suy nghĩ vẩn đục đang tồn tại trong t&acirc;m tr&iacute;, cậu ấy sẽ phải lựa chọn hướng đi đ&uacute;ng đắn cho bản th&acirc;n để c&oacute; thể chiển thắng những bản năng đang tồn tại ở ch&iacute;nh con người m&igrave;nh với hy vọng t&igrave;m thấy một l&yacute; tưởng y&ecirc;u cao đẹp trong cuộc đời. Cậu ấy sẽ phải l&agrave;m sao?<br />Những mi&ecirc;u tả ch&acirc;n thật c&oacute; lẽ sẽ khiến nhiều người sốc, nhưng sex kh&ocirc;ng phải l&agrave; chủ đạo của t&aacute;c phẩm n&agrave;y m&agrave; n&oacute; chỉ l&agrave; một trong những yếu tố g&oacute;p phần l&agrave;m nổi bật chủ đề của t&aacute;c phẩm:&rdquo;RANH GIỚI&rdquo;, nhấn mạnh sự tương phản giữa hai mảng s&aacute;ng tối trong mỗi một con người.<br />Cậu truyện cũng kh&ocirc;ng chỉ n&oacute;i về t&igrave;nh y&ecirc;u, m&agrave; c&ograve;n n&oacute;i về t&igrave;nh cảm gia đ&igrave;nh, t&igrave;nh bạn thuở học sinh &ndash; sinh vi&ecirc;n, v&agrave; tr&ecirc;n hết l&agrave; t&igrave;nh người&hellip;.t&igrave;nh người &nbsp;trong cả những g&oacute;c khuất ở tận c&ugrave;ng dưới đ&aacute;y x&atilde; hội, đằng sau những số phận nghiệt ng&atilde;&hellip;<br />H&atilde;y ki&ecirc;n tr&igrave; đọc v&agrave; cảm nhận&hellip;', 0, '2022-05-23 11:25:27', '2022-05-23 11:26:55'),
(18, 'quan-khu-nam-dong-binh-ca.jpg', 'Quân Khu Nam Đồng', 'quan-khu-nam-dong', 0, 'Bình Ca', 'TuSach.vn', 'Đang cập nhật', 'Khu tập thể Nam Đồng l&agrave; khu gia binh lớn nhất thủ đ&ocirc; H&agrave; Nội, được h&igrave;nh th&agrave;nh c&aacute;ch đ&acirc;y hơn 50 năm. L&agrave; nơi ở của hơn 500 gia đ&igrave;nh c&aacute;n bộ qu&acirc;n đội trung, cao cấp, hơn 70 vị tướng đ&atilde; từng sinh sống v&agrave; trưởng th&agrave;nh từ Khu tập thể Nam Đồng, nhiều gia đ&igrave;nh c&oacute; cả hai thế hệ &ldquo;tướng cha&rdquo; v&agrave; &ldquo;tướng con&rdquo;. Đ&acirc;y l&agrave; một khu gia binh điển h&igrave;nh, một đại gia đ&igrave;nh qu&acirc;n nh&acirc;n thu nhỏ thời chiến v&agrave; hậu chiến.<br />Qu&acirc;n khu Nam Đồng&nbsp;l&agrave;&nbsp;tiểu thuyết&nbsp;được viết bằng b&uacute;t ph&aacute;p hiện thực bởi một c&acirc;y b&uacute;t lần đầu ti&ecirc;n xuất hiện tr&ecirc;n văn đ&agrave;n, v&agrave; cũng l&agrave; một người con của khu tập thể Nam Đồng. T&aacute;c giả vừa l&agrave; nh&acirc;n chứng, vừa l&agrave; nh&acirc;n vật trực tiếp tham gia v&agrave;o c&aacute;c sự kiện; lại cũng l&agrave; người quan s&aacute;t tỉnh t&aacute;o c&oacute; độ l&ugrave;i thời gian để r&uacute;t ra những chi&ecirc;m nghiệm v&agrave; th&ocirc;ng điệp s&acirc;u sắc về một giai đoạn lịch sử đặc biệt của đất nước ch&uacute;ng ta.<br />Một cuốn s&aacute;ch l&ocirc;i cuốn, ly kỳ, hấp dẫn, khiến ta kh&oacute; rời mắt trước khi lật đến trang cuối c&ugrave;ng&hellip;Một cuốn s&aacute;ch sẽ mang tới rất nhiều tiếng cười v&agrave; lấy đi của ch&uacute;ng ta rất nhiều nước mắt&hellip;', 0, '2022-05-23 11:28:08', '2022-05-23 11:28:08'),
(26, 'con-cua-rung-xanh-edgar-rice-burroughs.jpg', 'Con của rừng xanh', 'con-cua-rung-xanh', 0, 'Edgar Rice Burroughs', 'TuSach.vn', 'Đang cập nhật', 'Tr&ecirc;n đường đi c&ocirc;ng t&aacute;c hu&acirc;n tước Clayton v&agrave; vợ &ndash; Alice bị bỏ lại tr&ecirc;n đảo hoang. Sau một tai nạn bất ngờ, cả hai c&ugrave;ng qua đời bỏ lại đứa con trai mới ba th&aacute;ng tuổi. Cậu b&eacute; được một con vượn nu&ocirc;i dưỡng v&agrave; đặt t&ecirc;n l&agrave; Tarzan.&nbsp;<br />Một lần t&igrave;nh cờ, một số người da trắng đ&atilde; bị &eacute;p v&agrave;o đảo hoang, nơi Tarzan sinh sống. Ch&agrave;ng trai hoang d&atilde; lần đầu ti&ecirc;n tiếp x&uacute;c với những người đồng loại, lần đầu ti&ecirc;n c&oacute; cảm x&uacute;c như con người nhưng đ&atilde; phải vội x&oacute;a nh&ograve;a, v&igrave; Jane &ndash; c&ocirc; g&aacute;i ch&agrave;ng y&ecirc;u đ&atilde; nhận lời cầu h&ocirc;n của ch&iacute;nh anh họ ch&agrave;ng. V&igrave; người con g&aacute;i ấy, ch&agrave;ng quyết từ bỏ tước vị cũng như nguồn gốc ch&iacute;nh thống của m&igrave;nh. Nhưng cũng v&igrave; người con g&aacute;i ấy, ch&agrave;ng đ&atilde; từ bỏ cuộc sống hoang d&atilde;, học c&aacute;ch trở lại th&agrave;nh một con người. Dưới sự gi&uacute;p đỡ của P&ocirc;n, ch&agrave;ng đ&atilde; t&igrave;m được một c&ocirc;ng việc tương đối ph&ugrave; hợp với t&iacute;nh c&aacute;ch của ch&agrave;ng, được đi khắp nơi, được quen biết nhiều người. Những mối quan hệ mới gi&uacute;p ch&agrave;ng kết giao được với nhiều bạn b&egrave;, nhưng cũng chuốc lấy kh&ocirc;ng &iacute;t kẻ th&ugrave;. Gặp lại Jane, sau một loạt những biến cố kinh khủng, cuối c&ugrave;ng hai người đ&atilde; trở th&agrave;nh vợ chồng v&agrave; Tarzan trở lại th&agrave;nh hu&acirc;n tước Clayton.&nbsp;', 0, '2022-05-23 11:49:25', '2022-05-23 11:49:52'),
(27, 'tro-lai-rung-gia-edgar-rice-burroughs.jpg', 'Trở Lại Rừng Già', 'tro-lai-rung-gia', 0, 'Edgar Rice Burroughs', 'TuSach.vn', 'Đang cập nhật', 'Tr&ecirc;n đường đi c&ocirc;ng t&aacute;c hu&acirc;n tước Clayton v&agrave; vợ &ndash; Alice bị bỏ lại tr&ecirc;n đảo hoang. Sau một tai nạn bất ngờ, cả hai c&ugrave;ng qua đời bỏ lại đứa con trai mới ba th&aacute;ng tuổi. Cậu b&eacute; được một con vượn nu&ocirc;i dưỡng v&agrave; đặt t&ecirc;n l&agrave; Tarzan.&nbsp;<br />Một lần t&igrave;nh cờ, một số người da trắng đ&atilde; bị &eacute;p v&agrave;o đảo hoang, nơi Tarzan sinh sống. Ch&agrave;ng trai hoang d&atilde; lần đầu ti&ecirc;n tiếp x&uacute;c với những người đồng loại, lần đầu ti&ecirc;n c&oacute; cảm x&uacute;c như con người nhưng đ&atilde; phải vội x&oacute;a nh&ograve;a, v&igrave; Jane &ndash; c&ocirc; g&aacute;i ch&agrave;ng y&ecirc;u đ&atilde; nhận lời cầu h&ocirc;n của ch&iacute;nh anh họ ch&agrave;ng. V&igrave; người con g&aacute;i ấy, ch&agrave;ng quyết từ bỏ tước vị cũng như nguồn gốc ch&iacute;nh thống của m&igrave;nh. Nhưng cũng v&igrave; người con g&aacute;i ấy, ch&agrave;ng đ&atilde; từ bỏ cuộc sống hoang d&atilde;, học c&aacute;ch trở lại th&agrave;nh một con người. Dưới sự gi&uacute;p đỡ của P&ocirc;n, ch&agrave;ng đ&atilde; t&igrave;m được một c&ocirc;ng việc tương đối ph&ugrave; hợp với t&iacute;nh c&aacute;ch của ch&agrave;ng, được đi khắp nơi, được quen biết nhiều người. Những mối quan hệ mới gi&uacute;p ch&agrave;ng kết giao được với nhiều bạn b&egrave;, nhưng cũng chuốc lấy kh&ocirc;ng &iacute;t kẻ th&ugrave;. Gặp lại Jane, sau một loạt những biến cố kinh khủng, cuối c&ugrave;ng hai người đ&atilde; trở th&agrave;nh vợ chồng v&agrave; Tarzan trở lại th&agrave;nh hu&acirc;n tước Clayton.&nbsp;', 0, '2022-05-23 11:50:58', '2022-05-23 11:50:58'),
(28, 'luat-cua-rung-gia-edgar-rice-burroughs.jpg', 'Luật Của Rừng Già', 'luat-cua-rung-gia', 0, 'Edgar Rice Burroughs', 'TuSach.vn', 'Đang cập nhật', 'Tr&ecirc;n đường đi c&ocirc;ng t&aacute;c hu&acirc;n tước Clayton v&agrave; vợ &ndash; Alice bị bỏ lại tr&ecirc;n đảo hoang. Sau một tai nạn bất ngờ, cả hai c&ugrave;ng qua đời bỏ lại đứa con trai mới ba th&aacute;ng tuổi. Cậu b&eacute; được một con vượn nu&ocirc;i dưỡng v&agrave; đặt t&ecirc;n l&agrave; Tarzan.&nbsp;<br />Một lần t&igrave;nh cờ, một số người da trắng đ&atilde; bị &eacute;p v&agrave;o đảo hoang, nơi Tarzan sinh sống. Ch&agrave;ng trai hoang d&atilde; lần đầu ti&ecirc;n tiếp x&uacute;c với những người đồng loại, lần đầu ti&ecirc;n c&oacute; cảm x&uacute;c như con người nhưng đ&atilde; phải vội x&oacute;a nh&ograve;a, v&igrave; Jane &ndash; c&ocirc; g&aacute;i ch&agrave;ng y&ecirc;u đ&atilde; nhận lời cầu h&ocirc;n của ch&iacute;nh anh họ ch&agrave;ng. V&igrave; người con g&aacute;i ấy, ch&agrave;ng quyết từ bỏ tước vị cũng như nguồn gốc ch&iacute;nh thống của m&igrave;nh. Nhưng cũng v&igrave; người con g&aacute;i ấy, ch&agrave;ng đ&atilde; từ bỏ cuộc sống hoang d&atilde;, học c&aacute;ch trở lại th&agrave;nh một con người. Dưới sự gi&uacute;p đỡ của P&ocirc;n, ch&agrave;ng đ&atilde; t&igrave;m được một c&ocirc;ng việc tương đối ph&ugrave; hợp với t&iacute;nh c&aacute;ch của ch&agrave;ng, được đi khắp nơi, được quen biết nhiều người. Những mối quan hệ mới gi&uacute;p ch&agrave;ng kết giao được với nhiều bạn b&egrave;, nhưng cũng chuốc lấy kh&ocirc;ng &iacute;t kẻ th&ugrave;. Gặp lại Jane, sau một loạt những biến cố kinh khủng, cuối c&ugrave;ng hai người đ&atilde; trở th&agrave;nh vợ chồng v&agrave; Tarzan trở lại th&agrave;nh hu&acirc;n tước Clayton.&nbsp;', 0, '2022-05-23 11:52:58', '2022-05-23 11:52:58'),
(29, 'an-tuong-sai-lam-jeffrey-archer.jpg', 'Ấn Tượng Sai Lầm', 'an-tuong-sai-lam', 0, 'Jeffrey Archer', 'TuSach.vn', 'Đang cập nhật', 'Với c&aacute;ch kể chuyện t&agrave;i hoa, Jeffrey Archer giống như đang chơi tr&ograve; m&egrave;o vờn chuột với độc giả. Si&ecirc;u phẩm của &ocirc;ng, v&igrave; thế đ&atilde; trở th&agrave;nh một trong những cuốn s&aacute;ch b&aacute;n chạy h&agrave;ng đầu New York.<br /><em>&ldquo;&hellip; Sau khi ra khỏi trung t&acirc;m th&agrave;nh phố, anh dừng lại để nh&igrave;n bản đồ. Anh quyết định vẫy một chiếc tắc xi, bởi v&igrave; anh sợ l&agrave; sẽ kh&oacute; kiếm tắc xi ở khu Berceni, khi anh cần phải cơ động. Một chiếc tắc xi cũng sẽ gi&uacute;p anh cắt đu&ocirc;i dễ d&agrave;ng hơn, bởi v&igrave; nếu c&oacute; một chiếc tắc xi n&agrave;o đ&oacute; đuổi theo anh, n&oacute; phải c&oacute; m&agrave;u v&agrave;ng, v&agrave; sẽ kh&ocirc;ng thể lẫn v&agrave;o đ&acirc;u được một khi ra đến v&ugrave;ng ngoại &ocirc;. Anh xem bản đồ một lần nữa, rẽ tr&aacute;i ở g&oacute;c phố tiếp theo, kh&ocirc;ng ngoảnh nh&igrave;n lại, thậm ch&iacute; cũng chẳng liếc nh&igrave;n v&agrave;o c&aacute;c cửa hiệu hai b&ecirc;n đường. Nếu c&ocirc; ta l&agrave; một kẻ chuy&ecirc;n nghiệp, việc anh ngoảnh nh&igrave;n lại ph&iacute;a sau sẽ l&agrave; một sai lầm nghi&ecirc;m trọng, bởi v&igrave; n&oacute; b&aacute;o cho c&ocirc; ta biết rằng anh đ&atilde; đề ph&ograve;ng. Anh vẫy một chiếc tắc xi&hellip;&rdquo;</em>', 0, '2022-05-23 11:57:22', '2022-05-23 11:57:22'),
(30, 'loi-nguyen-lo-ban-vien-thai-cuc.jpg', 'Lời Nguyền Lỗ Ban', 'loi-nguyen-lo-ban', 0, 'Viên Thái Cực', 'TuSach.vn', 'Đang cập nhật', 'T&aacute;c giả Vi&ecirc;n Th&aacute;i Cực, từ nhỏ đ&atilde; c&oacute; niềm hiếu kỳ đặc biệt đối với nguy&ecirc;n l&yacute; hoạt động của m&aacute;y m&oacute;c động cơ, hiện tại đang l&agrave; một chuy&ecirc;n gia kỹ thuật c&oacute; đẳng cấp, thường xuy&ecirc;n tiếp x&uacute;c, nghi&ecirc;n cứu v&agrave; l&agrave;m việc với những thiết bị tinh vi phức tạp. Những điều đ&oacute; đ&atilde; nu&ocirc;i dưỡng v&agrave; tiếp sức cho một sở th&iacute;ch rất kh&aacute;c người của &ocirc;ng, đ&oacute; l&agrave; t&igrave;m hiểu v&agrave; nghi&ecirc;n cứu về lĩnh vực trận ph&aacute;p &aacute;m kh&iacute;, cạm bẫy kỳ m&ocirc;n, m&agrave; giờ đ&acirc;y, hầu như chỉ c&ograve;n tồn tại trong s&aacute;ch vở v&agrave; truyền thuyết.<br />T&aacute;c giả Vi&ecirc;n Th&aacute;i Cực viết tiểu thuyết &ldquo;Lời nguyền Lỗ Ban&ldquo;, ban đầu chỉ l&agrave; nhằm thỏa m&atilde;n niềm say m&ecirc; bất tận của bản th&acirc;n. Sau khi nhận được sự ch&agrave;o đ&oacute;n nhiệt liệt của độc giả, t&aacute;c phẩm đ&atilde; được nh&agrave; xuất bản Văn Nghệ Giang T&ocirc; in ấn ph&aacute;t h&agrave;nh. Sau khi tập 1 ra mắt độc giả v&agrave;o năm 2011, cuốn tiểu thuyết đ&atilde; nhanh ch&oacute;ng tạo n&ecirc;n một cơn sốt tr&ecirc;n thị trường, được độc giả liệt v&agrave;o một trong ba cuốn tiểu thuyết thần kỳ nhất năm 2011. Nhưng kh&ocirc;ng giống với những cuốn tiểu thuyết kh&aacute;c, &ldquo;Lời nguyền Lỗ Ban&rdquo; đ&atilde; tự t&igrave;m cho m&igrave;nh một lối đi ri&ecirc;ng, chuy&ecirc;n khai th&aacute;c một đề t&agrave;i b&iacute; hiểm v&agrave; kh&oacute; khăn m&agrave; trước nay vẫn c&ograve;n bỏ ngỏ: cơ quan cạm bẫy, &aacute;m kh&iacute; kỳ m&ocirc;n. Kết hợp với một lượng kiến thức v&ocirc; c&ugrave;ng phong ph&uacute; m&agrave; t&aacute;c giả đ&atilde; t&iacute;ch luỹ được qua những th&aacute;ng ng&agrave;y say m&ecirc; với d&atilde; sử ch&iacute; qu&aacute;i, cổ tịch t&agrave;n văn, t&aacute;c phẩm như b&agrave;y ra trước mắt ch&uacute;ng ta một kho b&aacute;u v&ocirc; tận với những bảo vật lấp l&aacute;nh sắc m&agrave;u kỳ b&iacute; của thời gian, chỉ một ch&uacute;t lơ l&agrave; sẽ lập tức trầm m&igrave;nh v&agrave;o qu&ecirc;n l&atilde;ng. Ch&iacute;nh v&igrave; vậy, cuốn tiểu thuyết đ&atilde; được liệt v&agrave;o nh&oacute;m &ldquo;Tiểu thuyết tri thức&rdquo; c&ugrave;ng với những t&aacute;c phẩm đ&igrave;nh đ&aacute;m trước đ&oacute; như &ldquo;Mật m&atilde; T&acirc;y Tạng&ldquo;, &ldquo;Mật m&atilde; Sơn hải kinh&ldquo;, &ldquo;Đ&ocirc;ng y bất tử&rdquo;&hellip;', 0, '2022-05-23 12:07:10', '2022-05-23 12:07:10'),
(31, 'chuyen-quai-di-o-truong-hoc-quai-dam-hiep-hoi.jpg', 'Chuyện Quái Dị Ở Trường Học', 'chuyen-quai-di-o-truong-hoc', 0, 'Quái Đàm Hiệp Hội', 'TuSach.vn', 'Đang cập nhật', 'Bước v&agrave;o m&agrave;n sương m&ugrave;<br />Đ&ecirc;m tối.<br />C&ocirc; bước về nh&agrave; tr&ecirc;n con đường nhỏ, d&agrave;i v&agrave; hẹp.<br />Tiếng bước ch&acirc;n gấp g&aacute;p kh&ocirc;ng ngừng vang lại. Tuy biết r&otilde; đấy l&agrave; do m&igrave;nh tạo ra nhưng Nguyệt Quang vẫn cứ run.<br />T&ograve;a nh&agrave; cũ kĩ, &iacute;t n&oacute;i sống cũng đến h&agrave;ng trăm người. Gian nh&agrave; h&agrave;ng x&oacute;m lại như một con đường xa lạ, nh&agrave; n&agrave;o cũng cửa sắt kh&oacute;a chặt, c&aacute;ch biệt hẳn với b&ecirc;n ngo&agrave;i.<br />V&ocirc; nguy&ecirc;n cớ, Nguyệt Quang c&oacute; cảm gi&aacute;c theo bản năng rằng c&oacute; người đang b&aacute;m sau c&ocirc;. C&ocirc; nhanh &ndash; đối phương nhanh, c&ocirc; chậm &ndash; đối phương cũng chậm. Giống như một b&oacute;ng đen d&agrave;i k&eacute;o sau lưng c&ocirc;, muốn bỏ mặc cũng chẳng được. C&ocirc; dừng bước, &ocirc;m chặt lấy t&uacute;i c&ocirc;ng văn v&agrave;o l&ograve;ng.<br />Đường ph&iacute;a trong t&ograve;a nh&agrave; đột nhi&ecirc;n thổi qua một cơn gi&oacute;. Nguyệt Quang chỉ cảm thấy c&oacute; một cơn lạnh ph&iacute;a sau lưng. To&agrave;n th&acirc;n c&ocirc; l&ocirc;ng tơ dựng đứng l&ecirc;n. Cũng giống như trong một trường hợp n&agrave;o đ&oacute;, khi kh&ocirc;ng để &yacute; n&oacute; tấn c&ocirc;ng ngay v&agrave;o c&ocirc;. Như một sự ph&ograve;ng bị cần thiết, Nguyệt Quang đ&atilde; lựa chọn c&aacute;ch quay người lại nh&igrave;n. Trong gi&acirc;y ph&uacute;t ấy, c&ocirc; dường như nh&igrave;n thấy một b&oacute;ng đen. Khi c&ocirc; quay người lại cũng l&agrave; l&uacute;c n&oacute; lẩn nhanh v&agrave;o ng&otilde; rẽ.<br />Nhưng đ&oacute; chỉ l&agrave; dường như, Nguyệt Quang kh&ocirc;ng d&aacute;m khẳng định.', 0, '2022-05-23 12:09:14', '2022-05-23 12:09:14'),
(32, 'nhat-ky-phong-tro-ma-voz.jpg', 'Nhật Ký Phòng Trọ Ma', 'nhat-ky-phong-tro-ma', 0, 'Voz', 'TuSach.vn', 'Đang cập nhật', 'Chào các bạn, m&igrave;nh t&ecirc;n là H (kh&ocirc;ng đ&ecirc;̉ đích danh vì m&ocirc;̣t vài lí do, mong các bạn thứ l&ocirc;̃i) m&igrave;nh 23t, truy&ecirc;̣n m&igrave;nh sắp k&ecirc;̉ nghe gi&ocirc;́ng m&ocirc;̣t c&acirc;u truy&ecirc;̣n&nbsp;ma, kì bí, kinh dị nhưng m&igrave;nh cũng chẳng rõ chính xác nó là cái gì nữa, m&igrave;nh chỉ chia sẻ l&ecirc;n đ&acirc;y như m&ocirc;̣t quy&ecirc;̉n nh&acirc;̣t kí tự thu&acirc;̣t của m&igrave;nh v&ecirc;̀ những ngày kinh hoàng mà m&igrave;nh vừa trải qua (có lẽ nó v&acirc;̃n còn ti&ecirc;́p di&ecirc;̃n) và nghĩ rằng chắc các bạn hứng thú với nó (vì m&igrave;nh nghĩ ai mà chẳng thích m&ocirc;̣t c&acirc;u truy&ecirc;̣n kì dị, ma quái sảy ra tr&ecirc;n chính đời thực và được k&ecirc;̉ lại bởi chính nh&acirc;n v&acirc;̣t trong c&acirc;u truy&ecirc;̣n!) còn chuy&ecirc;̣n tin hay kh&ocirc;ng là tuỳ bạn.<br />Hi&ecirc;̣n giờ, tính từ ngày bắt đ&acirc;̀u dọn v&ocirc; căn phòng trọ &acirc;́y cũng được g&acirc;̀n 1 tu&acirc;̀n r&ocirc;̀i và m&igrave;nh đang khá r&ocirc;́i loạn v&ecirc;̀ t&acirc;m trí (cứ cho là gặp v&acirc;́n đ&ecirc;̀ v&ecirc;̀ th&acirc;̀n kinh) nhưng ít nh&acirc;́t nó cũng được coi là &ocirc;̉n so với những gì mà m&igrave;nh đã phải trải qua và có lẽ sẽ là bình thường với những gì sắp trải ra. B&acirc;y giờ m&igrave;nh sẽ k&ecirc;̉ cho các bạn nghe v&ecirc;̀ những ngày vừa r&ocirc;̀i và sẽ ti&ecirc;́p tục k&ecirc;̉ v&ecirc;̀ những gì đang sảy ra trong những update sau đó. Vozforums c&ugrave;ng sachvui.com hãy cùng đón nh&acirc;̣n những c&acirc;u truy&ecirc;̣n mà m&igrave;nh cho rằng bạn sẽ thích thú với nó&hellip;', 0, '2022-05-23 12:10:05', '2022-05-23 12:10:05'),
(33, 'mu-phu-thuy-gloria-ericson.jpg', 'Mụ Phù Thủy', 'mu-phu-thuy', 3, 'Gloria Ericson', 'TuSach.vn', 'Tạm ngừmg', '&ldquo;V&igrave; ch&uacute;ng t&ocirc;i kh&ocirc;ng t&igrave;m thấy người b&agrave; con n&agrave;o, trong khi dường như &ocirc;ng l&agrave; người li&ecirc;n lạc thư từ v&agrave; l&agrave; kh&aacute;ch thăm duy nhất của b&agrave; ấy n&ecirc;n ch&uacute;ng t&ocirc;i xin b&aacute;o cho &ocirc;ng biết l&agrave; b&agrave; Miriam Winters đ&atilde; qua đời. B&agrave; ấy chết thanh thản trong giấc ngủ v&agrave;o ng&agrave;y 25 vừa qua&rdquo;.&nbsp;<br />&Aacute;nh nắng đang chiếu qua m&agrave;nh cửa sổ văn ph&ograve;ng t&ocirc;i bỗng trở n&ecirc;n lạnh lẽo. T&ocirc;i đang đứng khi mở bức thư, b&acirc;y giờ, t&ocirc;i đ&atilde; ngồi xuống, xoay c&aacute;i ghế bọc da một v&ograve;ng, nh&igrave;n đăm đăm ra cửa sổ. Thế l&agrave;, sau c&ugrave;ng, b&agrave; ấy đ&atilde; chết. &ldquo;Kh&aacute;ch thăm duy nhất&rdquo;. Lạy Ch&uacute;a, kh&ocirc;ng hẳn thế ! Lần sau c&ugrave;ng gặp b&agrave; ấy l&agrave; khi n&agrave;o nhỉ ? Năm năm ? S&aacute;u năm ?', 0, '2022-05-23 12:11:27', '2022-05-23 12:12:30'),
(35, 'luoc-su-tuong-lai-yuval-noah-harari.jpg', 'Lược Sử Tương Lai', 'luoc-su-tuong-lai', 2, 'Yuval Noah Harari', 'TuSach.vn', 'Đang cập nhật', 'Homo sapiens c&oacute; phải l&agrave; một dạng sống si&ecirc;u đẳng, hay chỉ l&agrave; một tay đầu gấu địa phương? L&agrave;m thế n&agrave;o con người lại tin rằng họ kh&ocirc;ng chỉ đ&atilde; kiểm so&aacute;t thế giới, m&agrave; c&ograve;n mang lại &yacute; nghĩa cho n&oacute;? C&ocirc;ng nghệ sinh học v&agrave; tr&iacute; th&ocirc;ng minh nh&acirc;n tạo đe doạ lo&agrave;i người ra sao? Sinh vật n&agrave;o c&oacute; thể kế thừa lo&agrave;i người, v&agrave; t&ocirc;n gi&aacute;o mới n&agrave;o sẽ được sản sinh?<br />Với giọng kể cuốn h&uacute;t v&agrave; mới lạ,&nbsp;Harari&nbsp;sẽ dần gợi mở v&agrave; trả lời những c&acirc;u hỏi tr&ecirc;, nhờ ph&acirc;n t&iacute;ch chi tiết những luận điểm g&acirc;y nhiều tranh c&atilde;i: chủ nghĩa nh&acirc;n đạo l&agrave; một dạng t&ocirc;n gi&aacute;o, thứ t&ocirc;n gi&aacute;o t&ocirc;n thờ con người thay v&igrave; thần th&aacute;nh; sinh vật l&agrave; thuật to&aacute;n&hellip; &ocirc;ng vẽ ra một viễn cảnh tương lai khi Sapiens thất thế v&agrave; Dữ liệu gi&aacute;o trở th&agrave;nh một h&igrave;nh mẫu.&nbsp;HOMO DEUS&nbsp;c&ograve;n b&agrave;n s&acirc;u hơn về c&aacute;c năng lực m&agrave; con người đ&atilde; tự trang bị để sinh tồn v&agrave; tiến ho&aacute; th&agrave;nh một giống lo&agrave;i ngự trị tr&ecirc;n tr&aacute;i đất, để rồi ch&iacute;nh trong tiến tr&igrave;nh ho&agrave;n thiện v&agrave; n&acirc;ng cấp c&aacute;c năng lực ấy ch&uacute;ng ta sẽ bị truất quyền kiểm so&aacute;t bởi một sinh vật mới, mang t&ecirc;n&nbsp;Homo Deus.', 0, '2022-05-23 12:15:42', '2022-05-23 12:16:07'),
(36, 'tat-ca-chung-ta-deu-la-ca-neil-shubin.jpg', 'Tất Cả Chúng Ta Đều Là Cá', 'tat-ca-chung-ta-deu-la-ca', 3, 'Neil Shubin', 'TuSach.vn', 'Hoàn thành', 'Neil Shubin, nh&agrave; cổ sinh học v&agrave; gi&aacute;o sư về giải phẫu học, người đ&atilde; đồng kh&aacute;m ph&aacute; Tiktaalik, &ldquo;lo&agrave;i c&aacute; c&oacute; tay&rdquo;, kể c&acirc;u chuyện về cơ thể ch&uacute;ng ta m&agrave; bạn chưa bao giờ nghe trước đ&acirc;y. Qua nghi&ecirc;n cứu h&oacute;a thạch v&agrave; ADN của c&aacute;c loại sinh vật nguy&ecirc;n thủy, t&aacute;c giả Neil Shubin đ&atilde; cho thấy sự tương đồng về mặt cấu tạo cơ thể giữa người với c&aacute;c loại sinh vật kh&aacute;c, từ c&aacute; cho đến giun v&agrave; thậm ch&iacute; l&agrave; vi khuẩn. Bằng những nội dung th&uacute; vị, l&yacute; th&uacute;, Tất cả ch&uacute;ng ta đều l&agrave; c&aacute; đ&atilde; gi&uacute;p chủ đề sinh học v&agrave; cổ sinh học vốn v&ocirc; c&ugrave;ng phong ph&uacute;, đa dạng trở n&ecirc;n đặc biệt gần gũi v&agrave; cuốn h&uacute;t với độc giả, nhất l&agrave; c&aacute;c bạn trẻ.', 0, '2022-05-23 12:17:27', '2022-05-23 12:18:22'),
(37, 'my-thuat-ve-chan-dung-gia-bao.jpg', 'Mỹ Thuật Vẽ Chân Dung', 'my-thuat-ve-chan-dung', 5, 'Gia Bảo', 'TuSach.vn', 'Đang cập nhật', 'Quyển s&aacute;ch Mỹ Thuật Vẽ Ch&acirc;n Dung sẽ hướng dẫn người học c&aacute;ch cảm nhận về vẽ ch&acirc;n dung th&ocirc;ng qua những b&agrave;i học cụ thể:<br /><strong>I. Giải phẫu đầu v&agrave; năm gi&aacute;c quan</strong><br /><strong>II. Cấu tạo v&agrave; h&igrave;nh thể đặc trưng của ngũ quan (năm gi&aacute;c quan)</strong><br /><strong>III. Ph&acirc;n biệt tuổi v&agrave; giới t&iacute;nh của cơ đầu</strong><br /><strong>IV. Phương ph&aacute;p quan s&aacute;t để vẽ ch&acirc;n dung</strong><br /><strong>V. Phương ph&aacute;p vẽ ch&acirc;n dung</strong><br /><strong>VI. Ph&aacute;c họa kết cấu v&agrave; ph&aacute;c họa độ s&aacute;ng tối</strong><br /><strong>VII. Giải th&iacute;ch c&aacute;c bước tiến h&agrave;nh ph&aacute;c họa ch&acirc;n dung</strong><br /><strong>VIII. Ti&ecirc;u chuẩn đ&aacute;nh gi&aacute; v&agrave; y&ecirc;u cầu cơ bản khi thi vẽ ch&acirc;n dung</strong><br /><strong>IX. Phương &aacute;n giải quyết v&agrave; c&aacute;c vấn đề kh&aacute;c thường xuất hiện khi đ&aacute;nh gi&aacute; v&agrave; thi vẽ ch&acirc;n dung</strong><br /><strong>X. C&aacute;c yếu tố kh&aacute;c li&ecirc;n quan đến vấn đề thi vẽ ch&acirc;n dung</strong><br /><strong>XI. Đ&aacute;nh gi&aacute; t&aacute;c phẩm</strong><br />Đ&acirc;y l&agrave; t&agrave;i liệu tham khảo hữu &iacute;ch d&agrave;nh cho c&aacute;c sinh vi&ecirc;n đang theo học chuy&ecirc;n ng&agrave;nh mỹ thuật.', 0, '2022-05-23 12:19:52', '2022-05-23 12:22:08'),
(38, 'ky-thuat-danh-bong-ban-thanh-long.jpg', 'Kỹ Thuật Đánh Bóng Bàn', 'ky-thuat-danh-bong-ban', 0, 'Thanh Long', 'TuSach.vn', 'Đang cập nhật', 'C&ugrave;ng với sự ph&aacute;t triển của kỹ thuật m&ocirc;n b&oacute;ng b&agrave;n, sự biến h&oacute;a về đường b&oacute;ng, biến h&oacute;a về điểm rơi khi đ&aacute;nh b&oacute;ng cũng ng&agrave;y một ph&aacute;t triển kh&ocirc;ng ngừng. Điều n&agrave;y đ&ograve;i hỏi vận động vi&ecirc;n b&oacute;ng b&agrave;n cần phải di chuyển bước ch&acirc;n nhanh hơn để đảm bảo t&iacute;nh ch&iacute;nh x&aacute;c của động t&aacute;c chi tr&ecirc;n v&agrave; ph&aacute;t huy sở trường kỹ chiến thuật c&aacute; nh&acirc;n. Ngược lại, nếu như di chuyển bước kh&ocirc;ng tốt th&igrave; kh&ocirc;ng thể đảm bảo cho chi tr&ecirc;n thực hiện động t&aacute;c đ&aacute;nh b&oacute;ng ch&iacute;nh x&aacute;c. T&iacute;nh chuẩn x&aacute;c của bước ch&acirc;n v&agrave; chất lượng đ&aacute;nh b&oacute;ng c&oacute; ảnh hưởng trực tiếp tới hiệu quảcủa việc sử dụng kỹ thuật sở trường của vận động vi&ecirc;n. V&igrave; vậy để đ&aacute;nh b&oacute;ng b&agrave;n được tốt, nhất định phải nắm vững kỹ thuật di chuyển bước.', 0, '2022-05-23 12:23:17', '2022-05-23 12:23:17'),
(39, 'dac-nhan-tam-dale-carnegie.jpg', 'Đắc nhân tâm', 'dac-nhan-tam', 0, 'Dale Carnegie', 'TuSach.vn', 'Đang cập nhật', '<em><strong>Đắc nh&acirc;n t&acirc;m&nbsp;</strong>&ndash;&nbsp;<strong>How to win friends and Influence People&nbsp;</strong>&nbsp;của Dale Carnegie l&agrave; quyển s&aacute;ch nổi tiếng nhất, b&aacute;n chạy nhất v&agrave; c&oacute; tầm ảnh hưởng nhất của mọi thời đại. T&aacute;c phẩm đ&atilde; được chuyển ngữ sang hầu hết c&aacute;c thứ tiếng tr&ecirc;n thế giới v&agrave; c&oacute; mặt ở h&agrave;ng trăm quốc gia.&nbsp;</em><br /><em>Đ&acirc;y l&agrave; quyển s&aacute;ch duy nhất về thể loại self-help li&ecirc;n tục đứng đầu danh mục s&aacute;ch b&aacute;n chạy nhất (best-selling Books) do b&aacute;o The New York Times b&igrave;nh chọn suốt 10 năm liền. Ri&ecirc;ng bản tiếng Anh của s&aacute;ch đ&atilde; b&aacute;n được hơn 15 triệu bản tr&ecirc;n thế giới. T&aacute;c phẩm c&oacute; sức lan toả v&ocirc; c&ugrave;ng rộng lớn &ndash; d&ugrave; bạn đi đến bất cứ nơi đ&acirc;u, bất kỳ quốc gia n&agrave;o cũng đều c&oacute; thể nh&igrave;n thấy. T&aacute;c phẩm được đ&aacute;nh gi&aacute; l&agrave; quyển s&aacute;ch đầu ti&ecirc;n v&agrave; hay nhất, c&oacute; ảnh hưởng l&agrave;m thay đổi cuộc đời của h&agrave;ng triệu người tr&ecirc;n thế giới.</em><br />Kh&ocirc;ng c&ograve;n nữa kh&aacute;i niệm giới hạn,&nbsp;<strong>Đắc Nh&acirc;n T&acirc;m</strong>&nbsp;l&agrave; nghệ thuật thu phục l&ograve;ng người, l&agrave; l&agrave;m cho tất cả mọi người y&ecirc;u mến m&igrave;nh.&nbsp;<strong>Đắc nh&acirc;n t&acirc;m</strong>&nbsp;v&agrave; c&aacute;i T&agrave;i trong mỗi người ch&uacute;ng ta.&nbsp;<strong>Đắc Nh&acirc;n T&acirc;m&nbsp;</strong>trong &yacute; nghĩa đ&oacute; cần được thụ đắc bằng sự hiểu r&otilde; bản th&acirc;n, th&agrave;nh thật với ch&iacute;nh m&igrave;nh, hiểu biết v&agrave; quan t&acirc;m đến những người xung quanh để nh&igrave;n ra v&agrave; khơi gợi những tiềm năng ẩn khuất nơi họ, gi&uacute;p họ ph&aacute;t triển l&ecirc;n một tầm cao mới. Đ&acirc;y ch&iacute;nh l&agrave; nghệ thuật cao nhất về con người v&agrave; ch&iacute;nh l&agrave; &yacute; nghĩa s&acirc;u sắc nhất đ&uacute;c kết từ những nguy&ecirc;n tắc v&agrave;ng của Dale Carnegie.<br />Quyển s&aacute;ch&nbsp;<strong>Đắc nhắn t&acirc;m</strong>&nbsp;l&agrave; cuốn s&aacute;ch &ldquo;đầu ti&ecirc;n v&agrave; hay nhất mọi thời đại về nghệ thuật giao tiếp v&agrave; ứng xử&rdquo;, quyển s&aacute;ch đ&atilde; từng mang đến th&agrave;nh c&ocirc;ng v&agrave; hạnh ph&uacute;c cho h&agrave;ng triệu người tr&ecirc;n khắp thế giới.<br /><strong>Đắc Nh&acirc;n T&acirc;m</strong>&nbsp;l&agrave; cuốn s&aacute;ch gối đầu giường về đối nh&acirc;n xử thế.', 0, '2022-05-24 08:08:29', '2022-05-24 08:08:29');

